import{_ as a,c as e,o as i,a1 as t}from"./chunks/framework.BUQJ8Vm8.js";const f=JSON.parse('{"title":"强势","description":"","frontmatter":{"title":"强势","customTag":"read>心理学","editLink":true,"date":"2024.02.02"},"headers":[],"relativePath":"read/When-I-say-no_I-feel-guilty.md","filePath":"read/When-I-say-no_I-feel-guilty.md","lastUpdated":1744158305000}'),o={name:"read/When-I-say-no_I-feel-guilty.md"};function r(n,l,s,d,c,h){return i(),e("div",null,l[0]||(l[0]=[t('<h1 id="强势" tabindex="-1">强势 <a class="header-anchor" href="#强势" aria-label="Permalink to &quot;强势&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">INFO</p><p>状态: 读完 作者: 曼纽尔·J·史密斯 推荐指数: 🌟🌟🌟🌟 类别: 心理学, 职场成长 链接: <a href="https://weread.qq.com/web/bookDetail/49f32500715443a149f102f" target="_blank" rel="noreferrer">https://weread.qq.com/web/bookDetail/49f32500715443a149f102f</a></p></div><blockquote><p>学会做自己的行动的评判者，学会<strong>清醒、强势</strong>，学会不要被他人操控。</p></blockquote><h2 id="mark" tabindex="-1">Mark <a class="header-anchor" href="#mark" aria-label="Permalink to &quot;Mark&quot;">​</a></h2><ul><li>我们生活中会遇到各种问题，但我们都能解决并处理好它。</li><li>他人对你的操控无处不在。</li><li>不论用什么方式，只要有人限制你决断自己行事的权利，那么他就是在操控你。</li><li>人生只有我们才能对自己负责任。</li><li>很多选择，是可以由自己来进行决定的。</li><li>当一个人能影响你的情绪的时候，请注意不要被情绪所操控了。</li><li>适当情况下，表达自己的目标诉求。</li></ul><h2 id="强势十大法则" tabindex="-1">强势十大法则 <a class="header-anchor" href="#强势十大法则" aria-label="Permalink to &quot;强势十大法则&quot;">​</a></h2><ul><li>你有权坚持自己的行为、想法和情感，并对一切后果进行负责。</li><li>坚持你要做的，不必解释。</li><li>帮不到别人，不必内疚。</li><li>你有权改变你的想法。</li><li>犯错不可怕，但要承担后果。</li><li>你有权说“我不知道”。</li><li>要与人交往，但不要讨好。</li><li>你有权做出“不合逻辑”的决定。</li><li>你有权说“我不明白”。</li><li>你有权说“我不在乎”。</li></ul><h2 id="强势技巧表" tabindex="-1">强势技巧表 <a class="header-anchor" href="#强势技巧表" aria-label="Permalink to &quot;强势技巧表&quot;">​</a></h2><ol><li>“我是一张坏唱片”法：重复你的目的即可。</li><li>可行折中法：只要不损害个人利益或者准眼，可以跟他人讨价还价，达成自己的最终目标。</li><li>自由信息法：辨别他人流露的隐藏信息。</li><li>自我表露法：表达自己的观点/观念，引起他人注意。</li><li>迷惑法：承认他人的话有所道理。</li><li>否定决断法：承认自己事实上的不足，理性看待。</li><li>否定询问法：反问，利用批评意见或消除批评意见。</li></ol>',9)]))}const m=a(o,[["render",r]]);export{f as __pageData,m as default};
